export interface ISelectionManagerSearchParam {
    unitId: string;
    sheetId: string;
}

export enum SelectionMoveType {
    MOVE_START,
    MOVING,
    MOVE_END,
    /**
     * Events are not triggered by cursor movement
     */
    ONLY_SET,
}

