export interface IMoveRowsMutationParams {
    unitId: string;
    subUnitId: string;
    /**
     * The rows to be moved.
     */
    sourceRange: IRange;
    /**
     * The destination range to move the source rows to. Note that the range is before the movement has occurred.
     */
