export const handleMoveColsCommon = (params: IMoveColsCommand, targetRange: IRange) => {
    const { fromRange, toRange } = params.params || {};
    if (!fromRange || !toRange) {
        return [targetRange];
    }

    const fromCol = fromRange.startColumn;
